package net.minecraft.src;import java.util.Map;import net.minecraft.client.Minecraft;import net.minecraft.client.multiplayer.NetClientHandler;import net.minecraft.client.renderer.entity.RenderManager;import net.minecraft.client.settings.KeyBinding;import net.minecraft.network.INetworkManager;import net.minecraft.network.NetLoginHandler;import net.minecraft.network.packet.NetHandler;import net.minecraft.network.packet.Packet1Login;import net.minecraft.server.MinecraftServer;import cpw.mods.fml.common.network.IConnectionHandler;import cpw.mods.fml.common.network.NetworkMod;import cpw.mods.fml.common.network.Player;@NetworkMod(		clientSideRequired = true,		connectionHandler = mod_PFLM_PlayerFormLittleMaidFML.class		)public class mod_PFLM_PlayerFormLittleMaidFML extends BaseMod implements IConnectionHandler{	public String getVersion()	{		return "1.5.1-2a";	}	public void load()	{		ModLoader.setInGameHook(this, true, true);	}	public boolean onTickInGame(float f, Minecraft minecraft)	{		if (!mod_PFLM_PlayerFormLittleMaid.addRendererFlag) {			mod_PFLM_PlayerFormLittleMaid.addRendererFlag = true;			Map map = RenderManager.instance.entityRenderMap;			mod_PFLM_PlayerFormLittleMaid.mod_pflm_playerformlittlemaid.addRenderer(map);		}		return true;	}	@Override	public void playerLoggedIn(Player player, NetHandler netHandler,			INetworkManager manager) {	}	@Override	public String connectionReceived(NetLoginHandler netHandler,			INetworkManager manager) {		return null;	}	@Override	public void connectionOpened(NetHandler netClientHandler, String server,			int port, INetworkManager manager) {	}	@Override	public void connectionOpened(NetHandler netClientHandler,			MinecraftServer server, INetworkManager manager) {	}	@Override	public void connectionClosed(INetworkManager manager) {	}	@Override	public void clientLoggedIn(NetHandler clientHandler,			INetworkManager manager, Packet1Login login) {		if (mod_PFLM_PlayerFormLittleMaid.entityReplaceFlag) mod_PFLM_PlayerFormLittleMaid.mod_pflm_playerformlittlemaid.clientConnect((NetClientHandler) clientHandler);	}}