package modchu.pflmf;import java.util.LinkedList;import modchu.lib.Modchu_Debug;import modchu.lib.Modchu_Main;public class modc_PFLMF {	public modc_PFLMF() {	}	public String getVersion() {		return PFLMF_Main.version;	}	public void load() {		PFLMF_Main.load(this);	}	public boolean modEnabled() {		return Modchu_Main.isForge;	}	public void modsLoaded() {		PFLMF_Main.modsLoaded();	}	public boolean onTickInGame(byte by, Object... tickData) {		return PFLMF_Main.onTickInGame(by, tickData);	}	public static void sendState() {		PFLMF_Main.sendState();	}	public static Object[] getSendStateBytes(Object... o) {		return PFLMF_Main.getSendStateObjectArray((Object[]) o);	}	public static Object getPlayerState(int entityId, byte packetId) {		return PFLMF_Main.getPlayerState(entityId, packetId);	}	public void onPacketData(LinkedList list, Object entityPlayer, String channelName) {		Modchu_Debug.lDebug("modc_PFLMF 受信 onPacketData");		PFLMF_Main.onPacketData(list, entityPlayer, channelName);	}}