package modchu.pflmf;import modchu.lib.Modchu_Debug;import modchu.lib.Modchu_Main;import modchu.lib.characteristic.Modchu_Packet;public class modc_PFLMF {	public modc_PFLMF() {	}	public String getVersion() {		return PFLMF_Main.version;	}	public void load() {		Modchu_Debug.lDebug("modc_PFLMF load");		Modchu_Main.eventRegister(this);		Modchu_Packet.registerPacket(this, PFLMF_Main.packetChannelName);		PFLMF_Main.baseModInstance = this;		PFLMF_Main pflmf_Main = new PFLMF_Main();		pflmf_Main.load();		Modchu_Debug.lDebug("modc_PFLMF load end.");	}	public boolean modEnabled() {		return true;	}	public boolean onTickInGame(byte by, Object... tickData) {		return PFLMF_Main.onTickInGame(by, tickData);	}	public static void sendState() {		PFLMF_Main.sendState();	}	public static Object[] getSendStateBytes(Object... o) {		return PFLMF_Main.getSendStateObjectArray((Object[]) o);	}	public static Object getPlayerState(int entityId, byte packetId) {		return PFLMF_Main.getPlayerState(entityId, packetId);	}	public void onPacketData(Object packet, Object entityPlayer, String channelName) {		PFLMF_Main.onPacketData(packet, entityPlayer, channelName);	}}